import { Link } from 'react-router-dom'

const Post = ({ title, text, date, id }) => {


    return (
        <div className='post'>
            <h2>{title}</h2>
            <p>{date.toLocaleDateString()}</p>
            <p>{text}</p>
            <Link to={{
                pathname: `/home/posts/${id}`,
            }}>See post details</Link>
        </div>
    )
}

export default Post
