import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import useAuth from '../hooks/useAuth'
import axios from '../api/axios'

const CREATEPOST_URL = '/posts'


const PostForm = () => {
    const { auth } = useAuth()
    const navigate = useNavigate()

    const [title, setTitle] = useState('')
    const [text, setText] = useState('')
    const [published, setPublished] = useState('')
    const [readtime, setReadtime] = useState('')
    const [errMsg, setErrMsg] = useState('')

    const handleSubmit = async (e) => {
        e.preventDefault()
        try {
            const response = await axios.post(CREATEPOST_URL, 
                JSON.stringify({ title, text, readtime, published }),
                {
                    headers: {
                        'Content-Type': 'application/x-www-form-urlencoded',
                        'Authorization': `Bearer ${auth.accessToken}` 
                    }
                }
            )
            console.log(response)
            setText('')
            setPublished('')
            setReadtime('')
            navigate('/home')
        } catch (err) {
            if(!err?.response) {
                setErrMsg('No server resonse')
            } else {
                setErrMsg('Creation of blogpost failed')
            }
        }
    }

    return (
        <>
            <section className='create-post'>
                <h1>New blogpost</h1>
                <form onSubmit={handleSubmit}>
                    <input
                        type='text'
                        id='title'
                        placeholder='Title'
                        onChange={(e) => setTitle(e.target.value)}
                        value={title}
                    />
                    <textarea 
                        id='text'
                        placeholder='Your blogtext goes here..'
                    >
                    </textarea>
                    <div>
                        <label htmlFor='published'>Publish now: </label>
                        <input 
                        type='checkbox'
                        id='published'
                        onChange={(e) => (e.target.checked ? 'true' : 'false')}
                        />
                    </div>
                    <div>
                        <label htmlFor='readtime'>Readtime: </label>
                        <input
                            type='number'
                            id='readtime'
                            onChange={(e) => (e.target.value)}
                        />
                    </div>
                    <button id="create-post-button">Create post</button>
                    </form>
                    <p className={errMsg ? 'errmsg' : 'offscreen'}>{errMsg}</p>
            </section>
        </>
    )
}

export default PostForm
