import { Link } from 'react-router-dom'
import { format, parseISO } from 'date-fns'



const Post = ({ title, text, date, id, readtime }) => {

    return (
        <div className='post'>
            <h2>{title}</h2>
            <p>{format(parseISO(date), 'dd LLL yyyy' )}     {readtime} min read</p>
            <p>{text}</p>
            <Link to={{
                pathname: `/home/posts/${id}`,
            }}>Read post</Link>

        </div>
    )
}

export default Post
