import axios from '../api/axios'

const CommentForm = ({ id }) => {

    const [name, setName] = useState()
    const [comment, setComment] = useState()
    const [errMsg, setErrMsg] = useState('')

    // Submit new comment
    const addComment = async (e) => {
        e.preventDefault()
        try {
            const response = await axios.post(`/${id}/comments`,
                JSON.stringify({ name: name, comment: comment, post: id  }),
                {
                    headers: { 'Content-Type': 'application/json' }
                }
            )
            console.log(response)
            setName('')
            setComment('')
        } catch (err) {
            if(!err?.response) {
                setErrMsg('No server resonse')
            } else {
                console.log(err)
                setErrMsg('Creation of comment failed')
            }
        }
    }  

    return (
        <form className='add-comment' onSubmit={addComment}>
                        <label htmlFor='name'>Name</label>
                        <input
                            type='text'
                            id='name'
                            onChange={(e) => setName(e.target.value)}
                        />
                        <label htmlFor='comment'>Comment</label>
                        <input
                            type='text'
                            id='comment'
                            onChange={(e) => setComment(e.target.value)}
                        />
                        <button>Add</button>
                        <p className={errMsg ? 'errmsg' : 'offscreen'}>{errMsg}</p>
                    </form>
     )
}

export default CommentForm
